const myModule=(()=>{"use strict";let h=document.getElementById("result"),a=document.querySelector("#btnPerimetroCuadrado"),b=document.querySelector("#btnAreaCuadrado"),c=document.querySelector("#btnPerimetroTriangulo"),d=document.querySelector("#btnAreaTriangulo"),e=document.querySelector("#btnDiametroCirculo"),f=document.querySelector("#btnPerimetroCirculo"),g=document.querySelector("#btnAreaCirculo"),i=a=>4*a,j=a=>a*a;a.addEventListener("click",()=>{let a=Number(document.getElementById("inputLado").value),b=`El perimetro del cuadrado es: ${i(a)} cm`;h.innerText=b}),b.addEventListener("click",()=>{let a=Number(document.getElementById("inputLado").value),b=`El Area del cuadrado es: ${j(a)} cm^2`;h.innerText=b});let k=(a,b,c)=>a+b+c,l=(a,b)=>a*b/2;c.addEventListener("click",()=>{let a=document.getElementById("lado1T"),b=document.getElementById("lado2T"),c=document.getElementById("lado3T"),d=Number(a.value),e=Number(b.value),f=Number(c.value),g=`El perimetro del triangulo es de: ${k(d,e,f)} cm`;h.innerText=g}),d.addEventListener("click",()=>{let a=document.getElementById("base"),b=document.getElementById("altura"),c=Number(a.value),d=Number(b.value),e=`El area del triangulor es de: ${l(c,d)}`;h.innerText=e});let m=a=>2*a,n=a=>m(a)*Math.PI,o=a=>a*a*Math.PI;e.addEventListener("click",()=>{let a=Number(document.getElementById("radioC").value),b=`El diametro del circulo es: ${m(a)}`;h.innerText=b}),f.addEventListener("click",()=>{let a=Number(document.getElementById("radioC").value),b=`El Perimetro del circulo es: ${n(a)}`;h.innerText=b}),g.addEventListener("click",()=>{let a=Number(document.getElementById("radioC").value),b=`El Area del circulo es: ${o(a)}`;h.innerText=b})})()